char char_html[]= "\
<HTML>\
<HEAD>\
  <title>ZipIt</title>\
  <style>\
    body {\
      background-color: #FFFFFF;\
      color: black;\
      font-size: 100px;\
    }\
    table{\
      margin-left: auto;\
      margin-right: auto;\
    }\
    .rect {\
      width: 100%;\
      height: 100%;\
      border: none;\
      color: #000000;\
      padding: 0px 0px;\
      text-align: center;\
      text-decoration: none;\
      display: inline-block;\
      font-size: 35px;\
      margin: 0px 0px;\
      cursor: pointer;\
      font-family: monospace;\
    }\
    .WhiteBG {\
      background-color: #FFFFFF;\
    }\
    .BlueBG {\
      background-color: #6db7fc;\
    }\
    .GreenBG {\
      background-color: #5eff5e;\
    }\
    .GreyBG {\
      background-color: #888888;\
    }\
    .GreyBG15 {\
      background-color: #888888;\
      height: 15%;\
    }\
    .RedBG {\
      background-color: #ff2222;\
    }\
    .YellowBG {\
      background-color: #CCCC00;\
    }\
    .Title {\
      text-align: right;\
    }\
    .Units {\
      text-align: left;\
    }\
    .Debug {\
      color: #444;\
    }\
  </style>\
</head>\
<body>\
<br><br>\
<table width=100%>\
  <col width=50%>\
  <col width=50%>\
  <tr>\
    <td> <text class='rect WhiteBG Title'>Zip Time: &nbsp;</text> </td>\
    <td> <text id='zip_time' class='rect WhiteBG Units'>   x.xxx sec</text> </td>\
  </tr>\
</table>\
<font size=20px><br></font>\
<table width=50%>\
  <col width=100%>\
  <tr>\
    <td><text id='Reset' class='rect BlueBG'> RESET </text> </td>\
  </tr>\
</table>\
</body>\
<script>\
  document.getElementById('Reset').onclick = Reset;\
  let b_InitComplete = sessionStorage.getItem('b_InitComplete');\
  if (b_InitComplete != 'true') {\
    sessionStorage.setItem('b_InitComplete', 'true');\
    document.getElementById('zip_time').innerHTML = 0.000.toFixed(3).padStart(7, ' ') + ' sec';\
  }\
\
  main_loop();\
  setInterval(main_loop, 500);\
\
  function main_loop() {\
    let Cnt_Loops = sessionStorage.getItem('Cnt_Loops') * 1 + 1;\
    if (Cnt_Loops > 1) {\
      Cnt_Loops = 0;\
      let str_url = 'update';\
      var xhttp = new XMLHttpRequest();\
\
      xhttp.onreadystatechange = function () {\
        if (this.readyState == 4 && this.status == 200) {\
          let resp_json = JSON.parse(this.responseText);\
          document.getElementById('zip_time').innerHTML = parseFloat(resp_json.zip_time).toFixed(3).padStart(7, ' ') + ' sec';\
        }\
      };\
      xhttp.open('GET', str_url, true);\
      xhttp.send();\
    }\
    sessionStorage.setItem('Cnt_Loops', Cnt_Loops);\
  }\
\
  function Reset() {\
    sessionStorage.setItem('Cnt_Loops', 0);\
    document.getElementById('zip_time').innerHTML = 0.000.toFixed(3).padStart(7, ' ') + ' sec';\
  }\
\
  function beep() {\
    var snd = new Audio('data:audio/wav;base64,');\
    snd.play();\
  }\
\
</script>\
</html>\
";
